package network

import (
	"context"
	"fmt"
	"github.com/hashicorp/go-getter"
	"github.com/joho/godotenv"
	"io/ioutil"
	"net/http"
	"os"
)

const (
	AutoGeneratedEnvFileMessage = `##############################################################################################################################
#
# AUTO GENERATED FILE - please do not modify - modify node_conf.yaml instead
#
##############################################################################################################################
`
)

func downloadFile(src, dest string) error {
	client := &getter.Client{
		Ctx:  context.Background(),
		Src:  src,
		Dst:  dest,
		Dir:  true,
		Mode: getter.ClientModeFile,
	}
	if err := client.Get(); err != nil {
		return err
	}
	return nil
}

func downloadFileOverHttp(url string) ([]byte, error) {
	fmt.Println("Fetching bootnodes from", url)
	// Get the data
	resp, err := http.Get(url)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	return ioutil.ReadAll(resp.Body)
}

func GenerateEnvFile() error {
	fmt.Printf("Generating .env file from ")
	err := WriteEnvMap(GetEnvironmentConfig(), ".env")
	if err != nil {
		return err
	}
	return nil
}

func WriteEnvMap(envMap map[string]string, filename string) error {
	content, err := godotenv.Marshal(envMap)
	if err != nil {
		return err
	}
	file, err := os.Create(filename)
	if err != nil {
		return err
	}
	defer file.Close()

	_, err = file.WriteString(AutoGeneratedEnvFileMessage + "\n\n")
	if err != nil {
		return err
	}
	_, err = file.WriteString(content + "\n")
	if err != nil {
		return err
	}
	file.Sync()
	return err
}

func GenerateDefaultNodeConfigsIfDoesntExist(nodeName string, chain Chain) (isGenerated bool, err error) {
	if !FileExists(NodeConfigLocation) {
		var nodeConfig = GetDefaultNodeConfig(chain)
		nodeConfig.Node.Name = nodeName
		err := nodeConfig.WriteOrUpdateNodeConfig()
		if err != nil {
			return false, err
		} else {
			return true, nil
		}
	}
	return false, nil
}

func SetupNetwork(chain Chain) error {
	fmt.Printf("Setting up node for chain %s.\n", chain.String())
	clientVersion := BeaconClientPrysm

	if !IsChainSupported(chain) {
		return fmt.Errorf("the network %s does not exist or is not supported\n", chain.String())
	}

	return NewResourceDownloader(chain, clientVersion).DownloadAll()
}
